import React, { PureComponent } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';


const data = [
    {
    name: 'Juillet',
    uv: 2780,
  },
  {
    name: 'Aout',
    uv: 2780,
    pv: 3908,
    amt: 2000,
  },
  {
    name: 'Septembre',
    uv: 4000,
    pv: 2400,
    amt: 2400,
  },
  {
    name: 'Octobre',
    uv: 3000,
    pv: 1398,
    amt: 2210,
  },
  {
    name: 'Decembre',
    uv: 2000,
    pv: 9800,
    amt: 2290,
  },
];

export default function Avis(){
    return (
    <div class="avis">

                <h1>Avis</h1>
                <article class="avis_container">
                <div class="avis_entete">
                    <img class="photo_avis" src="photo_profil_defaut.png" alt=""/>
                    <div class="infos_avis">
                        <h2>Nom</h2>
                        <h2>Prenom</h2>
                        <h2>Note</h2>
                    </div>
                </div>
                    <p class="avis_content">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent malesuada eget elit ac tincidunt. Curabitur eget magna porttitor urna semper varius vitae nec felis. Aenean commodo ultricies enim, non efficitur urna pellentesque eget. 
                    </p>
            </article>
      <ResponsiveContainer width="60%" aspect={3}>
        <AreaChart
          width={500}
          height={400}
          data={data}
          margin={{
            top: 10,
            right: 30,
            left: 0,
            bottom: 0,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Area type="monotone" dataKey="uv" stroke="#8884d8" fill="#8884d8" />
        </AreaChart>
      </ResponsiveContainer>
      </div>
    );
  }
