import react from 'react';
import Vue from 'https://cdn.jsdelivr.net/npm/vue@2.6.0/dist/vue.esm.browser.js'

var titre = new Vue({
    el:"#contenu_titre",
    data:{
    titre:"test"
    }
})

    let submit = document.getElementById("submit");
    submit.onclick=function(){
        validate();
    }

    function validate()
    {
        var x= document.getElementById("mdp").value;
        var pass="123";
        var pass2="456";
        if(x==pass||x==pass2){
                document.getElementById("disponibilités").href = "cours.xlsx";
        }
        else{
            alert("mot de passe incorrect");
        }
    }

function Disponibilite() {
    return (
    <div className="disponibilite">
        <h1 class="title_disponibilite">Disponibilités </h1>
	        <p>Consultez les disponibilités en cliquant sur "Disponibilités" après avoir rentré et vérifié le mot de passe.</p>
	    		<div id="download">
		    		<input id="mdp" type="password" name="mdp" placeholder="MOT DE PASSE"/>
		    		<input id="submit" type="submit" name="verifier" value="Verifier le mot de passe"/>
		    		<a disabled="true" id="disponibilités" >Disponibilités</a>
		    	</div>
	    	<p>	Si aucun créneaux ne vous convient, contactez moi pour savoir si il y a eu des désistements.
	    </p>
    </div>
    
    );
}

export default Disponibilite;
