import React, { PureComponent } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';


const data = [
    {
    name: 'Juillet',
    uv: 2780,
  },
  {
    name: 'Aout',
    uv: 2780,
    pv: 3908,
    amt: 2000,
  },
  {
    name: 'Septembre',
    uv: 4000,
    pv: 2400,
    amt: 2400,
  },
  {
    name: 'Octobre',
    uv: 3000,
    pv: 1398,
    amt: 2210,
  },
  {
    name: 'Decembre',
    uv: 2000,
    pv: 9800,
    amt: 2290,
  },
];

export default function Avis(){
    return (
    <div class="avis">

                <h1>Avis</h1>
                <h2>Avis laissés</h2>
                <article class="container_avis">
                    <article class="photo_avis_laissé">
                    <img  src="photo_profil_defaut" alt=""/>
                    </article>
                    <article class="container_avis_nom_prenom">
                        <p >Nom Prenom</p>
                    <article class="note_avis_laissé" src="" alt=""></article>
                    </article>
                </article>
                    <div class="onglet"><p class="text">text</p></div>
                    
                <p class="avis_content">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Curabitur at mauris facilisis, accumsan ipsum eu, mollis elit. Duis vel erat cursus, tincidunt sapien vitae, varius nulla. Morbi et tortor id felis iaculis aliquet ut ac turpis. Vivamus ullamcorper urna fermentum, molestie dui sit amet, varius est. Cras commodo lacus ac nulla sollicitudin, in tincidunt dui sagittis. Vestibulum fermentum turpis id felis interdum interdum. Curabitur feugiat velit quis porta suscipit. Morbi venenatis, justo placerat porttitor placerat, tortor augue finibus mauris, id tempor tortor dolor quis augue. Nulla nec dapibus lorem, ut lobortis nunc. Quisque tincidunt volutpat nulla, pulvinar consectetur erat consectetur tincidunt.
                </p>
      <ResponsiveContainer width="60%" aspect={3}>
        <AreaChart
          width={500}
          height={400}
          data={data}
          margin={{
            top: 10,
            right: 30,
            left: 0,
            bottom: 0,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Area type="monotone" dataKey="uv" stroke="#8884d8" fill="#8884d8" />
        </AreaChart>
      </ResponsiveContainer>
      </div>
    );
  }
