import React, { PureComponent } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';


const data = [
    {
    name: 'Juillet',
    uv: 2780,
  },
  {
    name: 'Aout',
    uv: 2780,
    pv: 3908,
    amt: 2000,
  },
  {
    name: 'Septembre',
    uv: 4000,
    pv: 2400,
    amt: 2400,
  },
  {
    name: 'Octobre',
    uv: 3000,
    pv: 1398,
    amt: 2210,
  },
  {
    name: 'Decembre',
    uv: 2000,
    pv: 9800,
    amt: 2290,
  },
];

export default function Avis(){
    return (
      <ResponsiveContainer width="200px" aspect={3}>
        <AreaChart
          width={500}
          height={400}
          data={data}
          margin={{
            top: 10,
            right: 30,
            left: 0,
            bottom: 0,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Area type="monotone" dataKey="uv" stroke="#8884d8" fill="#8884d8" />
        </AreaChart>
      </ResponsiveContainer>
    );
  }
